import { connection } from '../../index'

export default class Demo extends Component {

    state = {
        content: '',
        DanhSachGheDangChon: [],
        DanhSachGheNguoiKhacChon: []
    }
    componentDidMount() {

    }

    componentDidMount() {
        connection.on("ReceiveMessage", (name, message) => {
            this.setState({ content: name + ": " + message })
        })
    }

    componentDidUpdate() {
