                    <div className="row">
                        <button onClick={() => {
                            alert(1)
                            connection.invoke("SendMessage", "loc", "hello abc");
                        }} id="sendButton">Test</button>


                        <div>{this.state.content}</div>
                    </div>
                </div>
            </section>
        )
    }
}
