import React, { Component } from "react";

export default class Modal extends Component {








  tinhTongTien = () => {
    let tongTien = this.props.cart.reduce((tt,spGH,index)=>{
      return tt += spGH.soLuong * spGH.donGia;
    },0)

    return tongTien.toLocaleString();

  }

  render() {
    return (
      <div
        className="modal fade"
        id="modelId"
        tabIndex={-1}
        role="dialog"
        aria-labelledby="modelTitleId"
        aria-hidden="true"
      >
        <div
          className="modal-dialog"
          style={{ maxWidth: "100%" }}
          role="document"
        >
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Giỏ hàng</h5>
              <button
                type="button"
                className="close"
                data-dismiss="modal"
                aria-label="Close"
              >
                <span aria-hidden="true">×</span>
              </button>
            </div>
            <div className="modal-body">
              <table className="table">
                <tr>
                  <td>Mã SP</td>
                  <td>Hình ảnh</td>
                  <td>Tên SP</td>
                  <td> Số lượng</td>
                  <td>Đơn giá</td>
                  <td>Thành tiền</td>
                </tr>
                {this.props.cart.map((product, index) => {
                  return (
                    <tr key={index}>
                      <td>{product.maSP}</td>
                      <td>
                        <img src={product.hinhAnh} width="30" height="30" />
                      </td>
                      <td>{product.tenSP}</td>
                      <td>
                        <button onClick={()=>{
                          //Xử lý tăng
                          this.props.tangGiamSoLuong(product.maSP,1);
                        }}>+</button>
                        {product.soLuong}
                        <button onClick={()=>{
                          //Xử lý giảm
                          this.props.tangGiamSoLuong(product.maSP,-1);
                        }}>-</button>

                      </td>
                      <td>{product.donGia.toLocaleString()}</td>
                      <td>{(product.soLuong * product.donGia).toLocaleString()}</td>
                      <td><button className="btn btn-danger" onClick={()=>{
                        //Nhận sự kiện deleteItem thông qua props tên deleteItem
                        this.props.deleteItem(product.maSP)
                      }}>Xoá</button></td>
                    </tr>
                  );
                })}
                <tfoot>
                  <tr>
                    <td colSpan="5"></td>
                    <td >Tổng tiền</td>
                    <td>{this.tinhTongTien()}</td>
                  </tr>
                </tfoot>
              </table>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
